    import React, { useState } from 'react';
    import styled from 'styled-components';
    import { Product } from '../types';
    import { getAllProducts, updateProduct, deleteProduct } from '../api/products';
    import CategoryFilter from '../components/CategoryFilter';
    import ProductCard from '../components/ProductCard';
    import SearchBar from '../components/SearchBar';

    const Container = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 20px;
    padding: 20px;
    `;

    const Title = styled.h1`
    font-size: 36px;
    margin-bottom: 20px;
    `;

    const Filters = styled.div`
    display: flex;
    justify-content: space-between;
    margin-bottom: 20px;

    @media (max-width: 768px) {
        flex-direction: column;
    }
    `;

    const ProductGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    `;

    const ProductPage: React.FC = () => {
    const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
    const [searchQuery, setSearchQuery] = useState<string>('');
    const [products, setProducts] = useState<Product[]>([]);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const fetchedProducts = await getAllProducts();
            setProducts(fetchedProducts);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSelectCategory = (category: string) => {
        setSelectedCategory(category);
    };

    const handleSearch = (query: string) => {
        setSearchQuery(query);
    };

    const handleUpdate = (id: number, updatedProduct: Product) => {
        // Lógica para atualizar o produto com o id especificado
        console.log('Update product with ID:', id, 'to:', updatedProduct);
    };

    const handleDelete = (id: number) => {
        // Lógica para deletar o produto com o id especificado
        console.log('Delete product with ID:', id);
    };

    const filteredProducts = products.filter((product: Product) => {
        const matchesCategory = !selectedCategory || product.category === selectedCategory;
        const matchesSearch =
            !searchQuery || product.title.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesCategory && matchesSearch;
    });

    const ProductPage: React.FC = () => {
        const [products, setProducts] = useState<Product[]>([]);
        // Restante do código...

        return (
            <Container>
                <Title>Produtos</Title>
                <Filters>
                    <CategoryFilter
                        categories={['Eletrônicos', 'Roupas', 'Acessórios']}
                        onSelectCategory={handleSelectCategory}
                    />
                    <SearchBar onSearch={handleSearch} />
                </Filters>
                <ProductGrid>
                    {filteredProducts.map((product: Product) => (
                        <Container>
                            {products.map(product => (
                                <ProductCard
                                    key={product.id}
                                    product={product}
                                    onUpdate={(updatedProduct) => handleUpdate(product.id, updatedProduct)}
                                    onDelete={() => handleDelete(product.id)}
                                />
                            ))}
                        </Container>

                    ))}
                </ProductGrid>
            </Container>
        );
    };
}
    export default ProductPage;
