    // src/pages/ProductPage.tsx
    import React, { useState } from 'react';
    import { Product } from '../types';
    import CategoryFilter from '../components/CategoryFilter';
    import ProductCard from '../components/ProductCard';
    import productsData from '../data/products'; // Supondo que os dados estão em um arquivo

    const ProductPage: React.FC = () => {
    const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

    const handleSelectCategory = (category: string) => {
        setSelectedCategory(category);
    };

    const filteredProducts = selectedCategory
        ? productsData.filter((product: Product) => product.category === selectedCategory)
        : productsData;

    return (
        <div>
        <h1>Produtos</h1>
        <CategoryFilter
            categories={['Eletrônicos', 'Roupas', 'Acessórios']} // Supondo categorias fixas
            onSelectCategory={handleSelectCategory}
        />
        <div>
            {filteredProducts.map((product: Product) => (
            <ProductCard key={product.id} product={product} />
            ))}
        </div>
        </div>
    );
    };

    export default ProductPage;
