    // src/pages/ProductPage.tsx
    import React, { useState } from 'react';
    import { Product } from '../types';
    import CategoryFilter from '../components/CategoryFilter';
    import ProductCard from '../components/ProductCard';
    import SearchBar from '../components/SearchBar'; // Verifique se o caminho está correto

    const ProductPage: React.FC = () => {
    const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
    const [searchQuery, setSearchQuery] = useState<string>('');

    const handleSelectCategory = (category: string) => {
        setSelectedCategory(category);
    };

    const handleSearch = (query: string) => {
        setSearchQuery(query);
    };

    const filteredProducts = productsData.filter((product: Product) => {
        const matchesCategory = !selectedCategory || product.category === selectedCategory;
        const matchesSearch = !searchQuery || product.title.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesCategory && matchesSearch;
    });

    return (
        <div>
        <h1>Produtos</h1>
        <CategoryFilter
            categories={['Eletrônicos', 'Roupas', 'Acessórios']}
            onSelectCategory={handleSelectCategory}
        />
        <SearchBar onSearch={handleSearch} /> {/* Certifique-se que está sendo usado corretamente */}
        <div>
            {filteredProducts.map((product: Product) => (
            <ProductCard key={product.id} product={product} />
            ))}
        </div>
        </div>
    );
    };

    export default ProductPage;
